export const getInitialProps = async ({ store, pathname, query }) => {
  console.log(store);
  console.log(pathname);
  console.log(query);
  store.dispatch({ type: "GET_META_DATA", payload: "was set in error page " });
  // const { page, id } = context.query;
  try {
    const api = serviceApiFormData();
    const res = await api.post("MenuApp/getMenu");
    const menu = res.data;
    // const data = {
    //   page: page,
    //   category_id: id,
    // };
    // const productList = await api.post("ListProduct/getProductList", data);
    // const product = productList.data;
    return {
      props: {
        product: [],
        menu,
      },
    };
  } catch {
    return {
      props: {
        product: {},
        menu: {},
      },
    };
  }
};

export default connect((state) => state)(ProductList);
